import React, { useContext } from 'react';
import { ThemeContext } from '../contexts/AttendanceContext';

const AppHeader = ({ stats, viewMode, setViewMode }) => {
  return (
    <header style={{
      display: 'flex',
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '16px',
      backgroundColor: colors.primary,
      borderBottom: `1px solid ${colors.border}`,
      gap: '16px'
    }}>
      <div style={{ 
        height: '40px',
        width: '160px',
        display: 'flex',
        alignItems: 'center'
      }}>
        <img 
          src={require('../assets/images/logo.png')} 
          alt="CenTap Logo" 
          style={{
            height: '100%',
            objectFit: 'contain'
          }}
        />
      </div>
      <div style={{ 
        display: 'flex', 
        alignItems: 'center',
        flexWrap: 'wrap',
        gap: '16px'
      }}>
        <div>
          <strong>{stats.present}</strong>/{stats.total} Present ({stats.rate}%)
        </div>
        <div style={{
          display: 'flex',
          backgroundColor: colors.hover,
          borderRadius: '4px',
          overflow: 'hidden'
        }}>
          <button 
            onClick={() => setViewMode('list')}
            style={{
              padding: '8px 12px',
              border: 'none',
              backgroundColor: viewMode === 'list' ? colors.secondary : 'transparent',
              color: viewMode === 'list' ? 'white' : colors.textPrimary,
              cursor: 'pointer'
            }}
          >
            List View
          </button>
          <button 
            onClick={() => setViewMode('grid')}
            style={{
              padding: '8px 12px',
              border: 'none',
              backgroundColor: viewMode === 'grid' ? colors.secondary : 'transparent',
              color: viewMode === 'grid' ? 'white' : colors.textPrimary,
              cursor: 'pointer'
            }}
          >
            Total View
          </button>
        </div>
      </div>
    </header>
  );
};

export default AppHeader;
