import React, { useContext } from 'react';
import { ThemeContext } from '../contexts/AttendanceContext';

const AppHeader = ({ stats, viewMode, setViewMode }) => {
  const { theme, isDarkMode, toggleTheme } = useContext(ThemeContext);

  return (
    <header style={{
      display: 'flex',
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '16px',
      backgroundColor: theme.primary,
      borderBottom: `1px solid ${theme.border}`,
      gap: '16px',
      transition: 'background-color 0.3s ease, border-color 0.3s ease'
    }}>
      <div style={{ 
        height: '40px',
        width: '160px',
        display: 'flex',
        alignItems: 'center'
      }}>
        <img 
          src={require('../assets/images/logo.png')} 
          alt="CenTap Logo" 
          style={{
            height: '100%',
            objectFit: 'contain',
            filter: isDarkMode ? 'brightness(1.2)' : 'none'
          }}
        />
      </div>
      <div style={{ 
        display: 'flex', 
        alignItems: 'center',
        flexWrap: 'wrap',
        gap: '16px'
      }}>
        <div style={{ color: theme.textPrimary }}>
          <strong>{stats.present}</strong>/{stats.total} Present ({stats.rate}%)
        </div>

        {/* View mode toggle */}
        <div style={{
          display: 'flex',
          backgroundColor: theme.hover,
          borderRadius: '4px',
          overflow: 'hidden'
        }}>
          <button 
            onClick={() => setViewMode('list')}
            style={{
              padding: '8px 12px',
              border: 'none',
              backgroundColor: viewMode === 'list' ? theme.secondary : 'transparent',
              color: viewMode === 'list' ? 'white' : theme.textPrimary,
              cursor: 'pointer'
            }}
          >
            List View
          </button>
          <button 
            onClick={() => setViewMode('grid')}
            style={{
              padding: '8px 12px',
              border: 'none',
              backgroundColor: viewMode === 'grid' ? theme.secondary : 'transparent',
              color: viewMode === 'grid' ? 'white' : theme.textPrimary,
              cursor: 'pointer'
            }}
          >
            Total View
          </button>
        </div>

        {/* Dark mode toggle */}
        <button
          onClick={toggleTheme}
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '36px',
            height: '36px',
            borderRadius: '50%',
            border: 'none',
            backgroundColor: theme.hover,
            color: theme.textPrimary,
            cursor: 'pointer',
            transition: 'background-color 0.3s ease'
          }}
          aria-label={isDarkMode ? 'Switch to light mode' : 'Switch to dark mode'}
        >
          {isDarkMode ? 'ðŸŒž' : 'ðŸŒ™'}
        </button>
      </div>
    </header>
  );
};

export default AppHeader;
