import React, { useState, useRef, useEffect } from 'react';
import StatusIndicator from './StatusIndicator';
import { colors } from '../assets/styles/colors';

const AttendanceCard = ({ attendee }) => {
  const { name, present, checkInTime, photoUrl, nfcTagId } = attendee;
  const [showPopup, setShowPopup] = useState(false);
  const cardRef = useRef(null);

  // Handle mouse enter/leave with a small delay to prevent flicker
  const handleMouseEnter = () => {
    setShowPopup(true);
    // Change background color on hover
    if (cardRef.current) {
      cardRef.current.style.backgroundColor = colors.hover;
    }
  };

  const handleMouseLeave = () => {
    setShowPopup(false);
    // Reset background color
    if (cardRef.current) {
      cardRef.current.style.backgroundColor = colors.primary;
    }
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      setShowPopup(false);
    };
  }, []);

  return (
    <div 
      ref={cardRef}
      style={{
        display: 'flex',
        alignItems: 'center',
        padding: '12px',
        borderBottom: `1px solid ${colors.border}`,
        backgroundColor: colors.primary,
        position: 'relative',
        cursor: 'pointer',
        transition: 'background-color 0.2s ease',
        zIndex: showPopup ? 10 : 1
      }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      className="attendance-card"
    >
      <div style={{ marginRight: '16px' }}>
        <StatusIndicator isPresent={present} />
      </div>
      <div style={{ flex: 1 }}>
        <div style={{ color: colors.textPrimary, fontWeight: 500 }}>{name}</div>
        {present && checkInTime && (
          <div style={{ color: colors.textSecondary, fontSize: '12px' }}>
            Checked in at {checkInTime.toLocaleTimeString()}
          </div>
        )}
      </div>

      {/* Popup that appears on hover */}
      {showPopup && (
        <div 
          style={{
            position: 'absolute',
            top: '-20px',
            right: '-300px',
            backgroundColor: 'white',
            padding: '16px',
            borderRadius: '8px',
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.15)',
            width: '280px',
            zIndex: 100,
            display: 'flex',
            alignItems: 'center',
            gap: '16px',
            pointerEvents: 'none' // So it doesn't interfere with mouse events
          }}
        >
          <img 
            src={photoUrl} 
            alt={name}
            style={{
              width: '80px',
              height: '80px',
              objectFit: 'cover',
              borderRadius: '50%',
              border: `3px solid ${present ? colors.success : colors.error}`
            }}
          />
          <div>
            <div style={{ 
              fontWeight: 'bold',
              fontSize: '16px',
              marginBottom: '4px'
            }}>
              {name}
            </div>
            <div style={{ 
              color: colors.textSecondary,
              fontSize: '14px',
              marginBottom: '4px'
            }}>
              ID: {nfcTagId}
            </div>
            <div style={{
              color: present ? colors.success : colors.error,
              fontWeight: 'bold',
              fontSize: '14px'
            }}>
              {present ? '✓ Present' : '✗ Absent'}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AttendanceCard;
