import React, { useContext, useState } from 'react';
import { AttendanceContext, ThemeContext } from '../contexts/AttendanceContext';
import AttendanceList from '../components/AttendanceList';
import TotalView from '../components/TotalView';
import NFCScanner from '../components/NFCScanner';
import AppHeader from '../components/AppHeader';

const Dashboard = () => {
  const { loading, getStats } = useContext(AttendanceContext);
  const { theme } = useContext(ThemeContext);
  const [viewMode, setViewMode] = useState('list'); // 'list' or 'grid'
  const stats = getStats();

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        backgroundColor: theme.appBg,
        color: theme.textPrimary
      }}>
        Loading...
      </div>
    );
  }

  return (
    <div style={{ 
      maxWidth: '1200px', 
      margin: '0 auto', 
      padding: '0 16px',
      transition: 'background-color 0.3s ease, color 0.3s ease'
    }}>
      <AppHeader stats={stats} viewMode={viewMode} setViewMode={setViewMode} />

      <div style={{ 
        display: 'flex', 
        flexDirection: 'row', 
        flexWrap: 'wrap',
        gap: '16px',
        marginTop: '16px'
      }}>
        <div style={{ 
          flex: '1 1 600px',
          minWidth: '0'
        }}>
          {viewMode === 'list' ? <AttendanceList /> : <TotalView />}
        </div>
        <div style={{ 
          flex: '0 1 350px',
          minWidth: '300px'
        }}>
          <NFCScanner />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
