// Light and dark theme colors
export const lightColors = {
  primary: '#FFFFFF',       // White background
  secondary: '#1A73E8',     // Blue accent
  textPrimary: '#202124',   // Dark text
  textSecondary: '#5F6368', // Secondary text
  success: '#34A853',       // Green for present
  error: '#EA4335',         // Red for absent
  border: '#DADCE0',        // Border color
  hover: '#F1F3F4',         // Hover state
  cardBg: '#FFFFFF',        // Card background
  appBg: '#F8F9FA',         // App background
  shadow: 'rgba(0, 0, 0, 0.1)' // Shadow color
};

export const darkColors = {
  primary: '#1E1E1E',       // Dark background
  secondary: '#4285F4',     // Brighter blue accent
  textPrimary: '#FFFFFF',   // White text
  textSecondary: '#AAAAAA', // Secondary text
  success: '#4CAF50',       // Slightly brighter green for present
  error: '#F44336',         // Slightly brighter red for absent
  border: '#333333',        // Border color
  hover: '#2D2D2D',         // Hover state
  cardBg: '#2D2D2D',        // Card background
  appBg: '#121212',         // App background
  shadow: 'rgba(0, 0, 0, 0.5)' // Shadow color
};

// Default to light mode colors
export const colors = lightColors;
